self.onInit = function() {

    self.ctx.$scope.data = self.ctx.defaultSubscription.data;
    
    audio_file.onchange = function() {
        console.log("Uploaded audio file");
        var files = this.files;
        try{
            var file = URL.createObjectURL(files[0]);
            console.log("see file",file)
            audio_player.src = file;
            var player = document.getElementById("audio_player");
            console.info('audio_source', audio_player);
            player.addEventListener("play", function () {
                audio_player.play();
                getData();
            
            });
        }
        catch(e){
            alert("Please choose a file");
        }
        
        function getData() {
            if(window.webkitAudioContext) {
                audioCtx = new window.webkitAudioContext();
            } else {
                audioCtx = new AudioContext({sampleRate: 16000})
            }
            //var source = audioCtx.createBufferSource();
            var request = new XMLHttpRequest();
            request.open('GET', file, true);
            request.responseType = 'arraybuffer';
            request.onload = function() {
                let audioData = request.response;
                audioCtx.decodeAudioData(audioData, function(buffer) {
                    var myBuffer = buffer;
                    var audio_data = myBuffer.getChannelData(0);
                    //var sample_rate = myBuffer.sampleRate;
                    console.info("audio data length",audio_data.length);
                    //audio array
                    var audio_array = []; 
                    for(var i=0;i<audio_data.length;i++){
                        audio_array.push(audio_data[i]);
                    }
                    console.info("audio array",audio_array);
                    self.ctx.deviceService
                        .getDeviceCredentials(self.ctx
                            .datasources[0].entityId)
                        .subscribe(function(response) {
                            url = "/api/v1/" + response.credentialsId + "/telemetry";
                            data = {
                                "input": audio_array
                            };
                            
                            console.log("widget upload audio is running");
                            $.ajax({
                                    url: url,
                                    type: 'post',
                                    data: JSON.stringify(data),
                                    headers: {
                                        'Content-Type': 'application/json'
                                    },
                                    dataType: 'json',
                                    success: function(postResponse) {
                                        console.info("telemetry done. " + postResponse);
                                    }
                                });
                        });
                },
                function(e){"Error with decoding audio data" + e.error});
            }
            request.send();
            console.log("Server should receive data");
        }
    };
};

self.onDataUpdated = function() {
    self.ctx.detectChanges();
}

