self.onInit = function() {

    self.ctx.$scope.data = self.ctx.defaultSubscription
         .data;

    const handleSuccess = function(stream) {

        //connect to audio player
        let audio = document.querySelector('audio')

        if ("srcObject" in audio) {
            audio.srcObject = stream;
        }
        else {   // Old version
            audio.src = window.URL.createObjectURL(stream);
        }

        let playAudio = document.getElementById('player');

        let mediaRecorder = new MediaRecorder(stream);
        // Pass the audio stream 

        //if clicking audio player recorder
        audio_recorder.addEventListener("play", function () {
            mediaRecorder.start();
            audio.play();
            console.log(mediaRecorder.state);
        });

        //if clicking pause on audio recorder
        audio_recorder.addEventListener("pause", function () {
            mediaRecorder.stop();
            audio.pause();
            console.log(mediaRecorder.state);
        });

       // Chunk array to store the audio data 
        var dataArray = []; 

        // If audio data available then push it to the chunk array
        mediaRecorder.ondataavailable = function (ev) {
          dataArray.push(ev.data);
        }

        // Convert the audio data in to blob after stopping the recording
        mediaRecorder.onstop = function (ev) {

          // blob of type mp3/wav
          var audioData = new Blob(dataArray, 
                    {type:'audio/*'});

          // After fill up the chunk array make it empty
          dataArray = [];
          // Creating audio url with reference 
          // of created blob named 'audioData'
          var audioSrc = window.URL
              .createObjectURL(audioData);

          // Pass the audio url to the 2nd video tag
          playAudio.src = audioSrc;
          //Pass data to server
          var audioCtx = new AudioContext({sampleRate: 16000})

            var request = new XMLHttpRequest();
            request.open('GET', audioSrc, true);
            request.responseType = 'arraybuffer';
            request.onload = function() {
                var audio_Data = request.response;
                audioCtx.decodeAudioData(audio_Data, function(buffer) {
                    var myBuffer = buffer;
                    var audio_data = myBuffer.getChannelData(0);
                    var audio_array = []; 
                    for(var i=0;i<audio_data.length;i++){
                        audio_array.push(parseFloat(audio_data[i]*32767));
                    }
                    console.info("audio array",audio_array);
                    self.ctx.deviceService
                        .getDeviceCredentials(self.ctx
                            .datasources[0].entityId)
                        .subscribe(function(response) {
                            url = "/api/v1/" + response.credentialsId + "/telemetry";
                            data = {
                                "input": audio_array
                            };

                            console.log("widget record audio is running");
                            $.ajax({
                                    url: url,
                                    type: 'post',
                                    data: JSON.stringify(data),
                                    headers: {
                                        'Content-Type': 'application/json'
                                    },
                                    dataType: 'json',
                                    success: function(postResponse) {
                                        console.info("telemetry done. " + postResponse);
                                    }
                                });
                        });
                },
                function(e){"Error with decoding audio data" + e.error});
            }
            request.send();
            console.log("Server should receive data");
        }
    };

    navigator.mediaDevices.getUserMedia({ audio: true })
        .then(handleSuccess).catch(function (err) {
            console.log(err.name, err.message);
      });
}

self.onDataUpdated = function() {
}

self.onResize = function() {
}

self.onDestroy = function() {
}

